create table fimaki_back_db.bko_contacto_persona (
    id_contacto_persona number
        generated by default as identity
    not null enable,
    id_persona          number not null enable,
    id_contacto         number not null enable,
    principal_sn        varchar2(1 char) default 'N' not null enable,
    creado_por          varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado              timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por      varchar2(100 char),
    modificado          timestamp(6) with local time zone
);

alter table fimaki_back_db.bko_contacto_persona
    add
        check ( principal_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.bko_contacto_persona
    add constraint bko_contacto_persona_pk
        primary key ( id_contacto_persona )
            using index (
                create unique index fimaki_back_db.bko_contacto_rol_persona_pk on
                    fimaki_back_db.bko_contacto_persona (
                        id_contacto_persona
                    )
            ) enable;

