create table fimaki_back_db.bko_empresa (
    id_empresa             number
        generated by default as identity
    not null enable,
    actividad_economica_rc varchar2(20 char) not null enable,
    fecha_constitucion     date not null enable,
    pagina_web             varchar2(200 char),
    estado_rc              varchar2(20 char) not null enable,
    id_persona             number not null enable,
    nombre_comercial       varchar2(200 char),
    tipo_sociedad_rc       varchar2(20 char) not null enable,
    tamano_rc              varchar2(20 char) not null enable,
    ciiu_rc                varchar2(20 char) not null enable,
    ambito_operacional_rc  varchar2(20 char) not null enable,
    regimen_tributario_rc  varchar2(20 char) not null enable,
    tipo_contribuyente_rc  varchar2(20 char) not null enable,
    creado_por             varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado                 timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por         varchar2(100 char),
    modificado             timestamp(6) with local time zone,
    score_cedente_rc       varchar2(20 char),
    score_pagador_rc       varchar2(20 char),
    id_archivo             number,
    archivo_nombre         varchar2(255 char),
    archivo_tipo_mime      varchar2(100 char),
    archivo_contenido      blob
);

alter table fimaki_back_db.bko_empresa
    add constraint bko_empresa_pk primary key ( id_empresa )
        using index enable;

