create table fimaki_back_db.bko_empresa_score (
    id_score       number
        generated by default as identity
    not null enable,
    id_empresa     number not null enable,
    tipo_score     varchar2(30 byte) not null enable,
    score_valor    varchar2(20 char),
    fuente_score   varchar2(100 byte),
    fecha_score    date default sysdate not null enable,
    observaciones  varchar2(500 byte),
    creado         timestamp(6) with local time zone default current_timestamp not null enable,
    creado_por     varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    modificado     timestamp(6) with local time zone,
    modificado_por varchar2(100 char),
    activo_sn      varchar2(1 char)
);

alter table fimaki_back_db.bko_empresa_score
    add
        check ( activo_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.bko_empresa_score
    add
        check ( tipo_score in ( 'CEDENTE', 'PAGADOR' ) ) enable;

alter table fimaki_back_db.bko_empresa_score add primary key ( id_score )
    using index enable;

