create table fimaki_back_db.variable_sistema (
    id_variable     number
        generated by default as identity
    not null enable,
    nombre_variable varchar2(20 char) not null enable,
    valor           varchar2(4000 char) not null enable,
    descripcion     varchar2(4000 char) not null enable,
    creado_por      varchar2(100 char) not null enable,
    creado          timestamp(6) with local time zone not null enable,
    modificado_por  varchar2(100 char),
    modificado      timestamp(6) with local time zone,
    activo_sn       char(1 byte) default 'S' not null enable,
    ambito_rc       varchar2(10 byte) default 'GLOBAL' not null enable
);

alter table fimaki_back_db.variable_sistema
    add
        check ( activo_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.variable_sistema add constraint variable_sistema_k1 unique ( nombre_variable )
    using index enable;

alter table fimaki_back_db.variable_sistema
    add constraint variable_sistema_pk primary key ( id_variable )
        using index enable;

