create table fimaki_sec_db.iam_usuario (
    id_usuario                    number
        generated by default as identity
    not null enable,
    nombre_usuario                varchar2(100 char) not null enable,
    correo                        varchar2(100 char) not null enable,
    nombres                       varchar2(100 char) not null enable,
    apellido_paterno              varchar2(100 char) not null enable,
    apellido_materno              varchar2(100 char),
    contrasena                    raw(256) not null enable,
    bloqueado_sn                  varchar2(1 char) not null enable,
    requiere_cambio_contrasena_sn varchar2(1 char) not null enable,
    contrasena_expira_en          timestamp(6) with local time zone,
    creado_por                    varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado                        timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por                varchar2(100 char),
    modificado                    timestamp(6) with local time zone,
    id_persona                    number
);

alter table fimaki_sec_db.iam_usuario
    add constraint iam_usuario_pk primary key ( id_usuario )
        using index enable;

alter table fimaki_sec_db.iam_usuario add constraint iam_usuario_uk1 unique ( nombre_usuario )
    using index enable;

alter table fimaki_sec_db.iam_usuario add constraint iam_usuario_uk2 unique ( correo )
    using index enable;

