create table fimaki_back_db.bko_archivo_factura (
    id_archivo_factura number
        generated by default as identity
    not null enable,
    id_factura         number not null enable,
    id_archivo         number not null enable,
    atributo1          varchar2(100 byte),
    atributo2          varchar2(100 byte),
    atributo3          varchar2(100 byte),
    atributo4          varchar2(100 byte),
    atributo5          varchar2(100 byte),
    secuencia          number,
    creado             timestamp(6) with local time zone default current_timestamp not null enable,
    creado_por         varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    modificado         timestamp(6) with local time zone,
    modificado_por     varchar2(100 char)
);

alter table fimaki_back_db.bko_archivo_factura add primary key ( id_archivo_factura )
    using index enable;


