create table fimaki_back_db.bko_cuenta_bancaria (
    id_cuenta_bancaria number
        generated by default as identity
    not null enable,
    numero_cuenta      varchar2(20 char) not null enable,
    cci                varchar2(20 char) not null enable,
    alias              varchar2(20 char),
    es_principal_sn    varchar2(1 char) not null enable,
    id_persona         number not null enable,
    id_institucion     number not null enable,
    id_pais            number not null enable,
    creado_por         varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado             timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por     varchar2(100 char),
    modificado         timestamp(6) with local time zone,
    moneda_rc          varchar2(20 char) not null enable,
    tipo_cuenta_rc     varchar2(20 char),
    activo_sn          char(1 char) default 'S' not null enable
);

alter table fimaki_back_db.bko_cuenta_bancaria
    add
        check ( activo_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.bko_cuenta_bancaria
    add constraint bko_cuenta_bancaria_pk primary key ( id_cuenta_bancaria )
        using index enable;

alter table fimaki_back_db.bko_cuenta_bancaria
    add constraint bko_cuenta_bancaria_uk1 unique ( id_institucion,
                                                    numero_cuenta )
        using index enable;

alter table fimaki_back_db.bko_cuenta_bancaria add constraint bko_cuenta_bancaria_uk2 unique ( cci )
    using index enable;


