create table fimaki_back_db.bko_deposito_comprobante (
    id_comprobante    number
        generated by default as identity
    not null enable,
    id_deposito       number not null enable,
    archivo_nombre    varchar2(255 char),
    archivo_tipo_mime varchar2(100 char),
    archivo_contenido blob,
    usuario_registro  varchar2(100 char),
    creado_por        varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado            timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por    varchar2(100 char),
    modificado        timestamp(6) with local time zone,
    activo_sn         char(1 byte) default 'S' not null enable
);

alter table fimaki_back_db.bko_deposito_comprobante
    add
        check ( activo_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.bko_deposito_comprobante add primary key ( id_comprobante )
    using index enable;


