create table fimaki_back_db.bko_direccion_persona (
    id_direccion_persona number
        generated by default as identity
    not null enable,
    direccion            varchar2(200 char) not null enable,
    telefono_principal   varchar2(20 char),
    telefono_secundario  varchar2(20 char),
    id_persona           number not null enable,
    activo_sn            varchar2(1 char) default 'S' not null enable,
    id_pais              number not null enable,
    id_subdivision_n1    number not null enable,
    id_subdivision_n2    number not null enable,
    creado_por           varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado               timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por       varchar2(100 char),
    modificado           timestamp(6) with local time zone,
    principal_sn         varchar2(1 char) default 'S' not null enable,
    id_subdivision_n3    number
);

alter table fimaki_back_db.bko_direccion_persona
    add
        check ( activo_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.bko_direccion_persona
    add
        check ( principal_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.bko_direccion_persona
    add constraint bko_direccion_persona_pk primary key ( id_direccion_persona )
        using index enable;


