create table fimaki_back_db.bko_institucion_financiera (
    id_institucion   number
        generated by default as identity
    not null enable,
    nombre           varchar2(100 char) not null enable,
    siglas           varchar2(10 char),
    codigo_regulador varchar2(20 char),
    tipo_entidad_rc  varchar2(10 char),
    moneda_base_rc   varchar2(10 char) not null enable,
    id_pais          number not null enable,
    activo_sn        varchar2(1 char) default 'S' not null enable,
    creado_por       varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado           timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por   varchar2(100 char),
    modificado       timestamp(6) with local time zone
);

alter table fimaki_back_db.bko_institucion_financiera
    add
        check ( activo_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.bko_institucion_financiera
    add constraint bko_institucion_financiera_pk primary key ( id_institucion )
        using index enable;


