create table fimaki_back_db.bko_persona (
    id_persona        number
        generated by default as identity
    not null enable,
    tipo_persona_rc   varchar2(10 char),
    tipo_documento_rc varchar2(10 char),
    numero_doc        varchar2(20 char) not null enable,
    nombres           varchar2(100 char),
    apellido_paterno  varchar2(100 char),
    apellido_materno  varchar2(100 char),
    razon_social      varchar2(200 char),
    fecha_nacimiento  date,
    estado_rc         varchar2(10 char),
    id_pais           number not null enable,
    id_subdivision_n1 number,
    id_subdivision_n2 number,
    creado_por        varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado            timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por    varchar2(100 char),
    modificado        timestamp(6) with local time zone,
    origen_fondos_rc  varchar2(20 char)
);

alter table fimaki_back_db.bko_persona
    add constraint bko_persona_pk primary key ( id_persona )
        using index enable;

alter table fimaki_back_db.bko_persona
    add constraint bko_persona_uk1 unique ( tipo_documento_rc,
                                            numero_doc )
        using index enable;


