create table fimaki_back_db.bko_rol_persona (
    id_rol_persona number
        generated by default as identity
    not null enable,
    id_persona     number not null enable,
    tipo_rol_rc    varchar2(20 char) not null enable,
    fecha_inicio   timestamp(6) with local time zone not null enable,
    fecha_fin      timestamp(6) with local time zone,
    activo_sn      varchar2(1 char) default 'S' not null enable,
    creado_por     varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado         timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por varchar2(100 char),
    modificado     timestamp(6) with local time zone
);

alter table fimaki_back_db.bko_rol_persona
    add
        check ( activo_sn in ( 'S', 'N' ) ) enable;

alter table fimaki_back_db.bko_rol_persona
    add constraint bko_rol_persona_pk primary key ( id_rol_persona )
        using index enable;

alter table fimaki_back_db.bko_rol_persona
    add constraint bko_rol_persona_uk1 unique ( id_persona,
                                                tipo_rol_rc )
        using index enable;


