create table fimaki_back_db.bko_score_empresa (
    id_score           number
        generated by default as identity
    not null enable,
    id_rol_persona     number not null enable,
    fecha_reevaluacion date not null enable,
    activo_sn          varchar2(1 char) not null enable,
    creado_por         varchar2(100 char) default sys_context('USERENV', 'SESSION_USER') not null enable,
    creado             timestamp(6) with local time zone default current_timestamp not null enable,
    modificado_por     varchar2(100 char),
    modificado         timestamp(6) with local time zone,
    score_rc           varchar2(20 byte)
);

alter table fimaki_back_db.bko_score_empresa
    add constraint bko_score_empresa_pk primary key ( id_score )
        using index enable;


