create table fimaki_back_db.subdivision_geografica (
    id_subdivision       number
        generated by default as identity
    not null enable,
    id_pais              number not null enable,
    id_subdivision_padre number,
    nombre               varchar2(100 char) not null enable,
    tipo_rc              varchar2(6 char) not null enable,
    nivel                number(1, 0) not null enable,
    activo_sn            varchar2(1 char) not null enable,
    creado_por           varchar2(100 char) not null enable,
    creado               timestamp(6) with local time zone not null enable,
    modificado_por       varchar2(100 char),
    modificado           timestamp(6) with local time zone
);

alter table fimaki_back_db.subdivision_geografica
    add constraint subdivision_geografica_pk primary key ( id_pais,
                                                           id_subdivision )
        using index enable;


