create table fimaki_inv_db.inv_usuario_temporal (
    id_usuario_temp          number
        generated by default as identity
    not null enable,
    tipo_persona_rc          varchar2(10 char) not null enable,
    tipo_documento_rc        varchar2(10 char) not null enable,
    numero_documento         varchar2(20 char),
    razon_social             varchar2(200 char),
    nombres                  varchar2(100 char),
    apellido_paterno         varchar2(100 char),
    apellido_materno         varchar2(100 char),
    fecha_nacimiento         date,
    correo                   varchar2(100 char),
    celular                  varchar2(20 char),
    tipo_rol_rc              varchar2(10 char) not null enable,
    fecha_registro           timestamp(6) with local time zone,
    fecha_expiracion         timestamp(6) with local time zone,
    estado_rc                varchar2(10 char) default 'PENDIENTE' not null enable,
    ip_origen                varchar2(45 char),
    user_agent               varchar2(200 char),
    observaciones            varchar2(200 char),
    version_terminos         varchar2(10 char),
    version_datos_personales varchar2(10 char),
    version_contrato_mandato varchar2(10 char),
    fecha_aceptacion         timestamp(6) with local time zone default current_timestamp,
    id_pais                  number,
    id_subdivision_n1        varchar2(50 char),
    id_subdivision_n2        varchar2(50 char),
    contrasena               raw(256) not null enable
);

alter table fimaki_inv_db.inv_usuario_temporal
    add constraint inv_usuario_temp_pk primary key ( id_usuario_temp )
        using index enable;


